  const { climateTemperature, setClimateTemperature, climateHumidity, setClimateHumidity } = useClimateContext();

  const { desiredTemperature, setDesiredTemperature } = useState(climateTemperature);

  let returnId = '';
  useEffect(() => {
    if (climateTemperature != desiredTemperature) {
      clearInterval(returnId);
    } else {
      returnId = setInterval(() => {
        setClimateTemperature(climateTemperature+1);
      }, 1000)
    }
  }, [climateTemperature])

